    public void populateCvFolder(CsApiBanOutput csApiBanOutput) throws FMLManipulationException {
        super.populateCvFolder(csApiBanOutput);

        // Special mapping (ie copy between not same-named fields) from CsApiBan output to this buffer.
        // TODO: How do we know which fields to map to which others?
            this.set_ROWCOUNT(csApiBanOutput.get_PESNBUFROWCOUNT());
        for (int i = 0; i < csApiBanOutput.get_PHYDEVARRAYEXPIRATION_DATE_size(); i++) {
            this.set_EXPIRATION_DATE(i, csApiBanOutput.get_PHYDEVARRAYEXPIRATION_DATE(i));
        }
        for (int i = 0; i < csApiBanOutput.get_PHYDEVARRAYEFFECTIVE_DATE_size(); i++) {
            this.set_EFFECTIVE_DATE(i, csApiBanOutput.get_PHYDEVARRAYEFFECTIVE_DATE(i));
        }
        for (int i = 0; i < csApiBanOutput.get_PHYDEVARRAYDEALER_CODE_size(); i++) {
            this.set_DEALER_CODE(i, csApiBanOutput.get_PHYDEVARRAYDEALER_CODE(i));
        }

        // General mapping (ie copy between same-named fields) from CsApiBan output to this buffer.
        ServiceParameter.copyFmlBuffer(csApiBanOutput, this);
    }

    private void set_ROWCOUNT(Integer value) throws FMLManipulationException {
        this.fmlBuffer[1].setValue(0, value);
    }

    private void set_EXPIRATION_DATE(int nIndex, String value) throws FMLManipulationException {
        this.fmlBuffer[6].setValue(nIndex, value);
    }

    private void set_EFFECTIVE_DATE(int nIndex, String value) throws FMLManipulationException {
        this.fmlBuffer[7].setValue(nIndex, value);
    }

    private void set_DEALER_CODE(int nIndex, String value) throws FMLManipulationException {
        this.fmlBuffer[46].setValue(nIndex, value);
    }
